import React, { useState, useEffect, useCallback } from "react";

function AdminProducts({ token, onLogout }) {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [newProduct, setNewProduct] = useState({
    title: "",
    description: "",
    price: "",
    product_type: "TOTEBAG",
    available_sizes: "",
    images: [],
  });
  const [previewImages, setPreviewImages] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [removedImageIds, setRemovedImageIds] = useState([]);
  const [imageColors, setImageColors] = useState({});
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [productToDelete, setProductToDelete] = useState(null);
  const [showDescriptionPopup, setShowDescriptionPopup] = useState(false);
  const [currentDescription, setCurrentDescription] = useState("");
  const [currentImageIndex, setCurrentImageIndex] = useState({});

  const fetchProducts = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await fetch(
        "https://aymen88.pythonanywhere.com/api/products/",
        {
          headers: {
            Authorization: `Token ${token}`,
          },
        }
      );
      const data = await response.json();
      if (response.ok) {
        setProducts(data);
        // Initialize current image index for each product
        const indexes = {};
        data.forEach(product => {
          indexes[product.id] = 0;
        });
        setCurrentImageIndex(indexes);
      } else {
        setError(data.detail || "Failed to fetch products.");
      }
    } catch (err) {
      setError("Network error or server unavailable.");
      console.error("Error fetching products:", err);
    } finally {
      setIsLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  const handleShowDescription = (description) => {
    setCurrentDescription(description);
    setShowDescriptionPopup(true);
  };

  const handleNextImage = (productId) => {
    setCurrentImageIndex(prev => ({
      ...prev,
      [productId]: (prev[productId] + 1) % products.find(p => p.id === productId).images.length
    }));
  };

  const handlePrevImage = (productId) => {
    setCurrentImageIndex(prev => ({
      ...prev,
      [productId]: (prev[productId] - 1 + products.find(p => p.id === productId).images.length) %
        products.find(p => p.id === productId).images.length
    }));
  };

  // ... (keep all your existing functions unchanged until the return statement)

  return (
    <div className="admin-products">
      <button onClick={onLogout} className="logout-button">
        Logout
      </button>
      <h2>{editingProduct ? "Edit Product" : "Add New Product"}</h2>
      {error && <div className="error-message">{error}</div>}

      {/* Product Form (keep your existing form code) */}

      <div className="product-list">
        <h3>Existing Products</h3>
        {isLoading && <p>Loading products...</p>}
        {products.length === 0 && !isLoading && <p>No products added yet.</p>}
        {!isLoading && products.length > 0 && (
          <div className="products-grid">
            {products.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-image-container">
                  <div className="product-image-slider">
                    {product.images && product.images.length > 0 ? (
                      <div className="product-image-slide" style={{
                        transform: `translateX(-${currentImageIndex[product.id] * 100}%)`
                      }}>
                        <img
                          src={product.images[currentImageIndex[product.id]].image}
                          alt={product.title}
                          className="product-thumbnail"
                        />
                      </div>
                    ) : (
                      <div className="no-image-placeholder">No images</div>
                    )}
                  </div>
                  {product.images && product.images.length > 1 && (
                    <>
                      <button
                        className="carousel-arrow left"
                        onClick={() => handlePrevImage(product.id)}
                      >
                        &lt;
                      </button>
                      <button
                        className="carousel-arrow right"
                        onClick={() => handleNextImage(product.id)}
                      >
                        &gt;
                      </button>
                      <div className="carousel-dots">
                        {product.images.map((_, index) => (
                          <button
                            key={index}
                            className={`carousel-dot ${index === currentImageIndex[product.id] ? 'active' : ''}`}
                            onClick={() => setCurrentImageIndex(prev => ({
                              ...prev,
                              [product.id]: index
                            }))}
                          />
                        ))}
                      </div>
                    </>
                  )}
                </div>
                <div className="product-info">
                  <h4>{product.title}</h4>
                  <p className="product-price">${product.price}</p>
                  {product.product_type === "TSHIRT" && product.available_sizes && (
                    <p className="product-sizes">Sizes: {product.available_sizes}</p>
                  )}
                  <button
                    className="description-btn"
                    onClick={() => handleShowDescription(product.description)}
                  >
                    View Description
                  </button>
                </div>
                <div className="product-actions">
                  <button onClick={() => handleEdit(product)} className="edit-btn">
                    Edit
                  </button>
                  <button
                    onClick={() => {
                      setProductToDelete(product.id);
                      setShowDeleteModal(true);
                    }}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {showDescriptionPopup && (
        <div className="description-popup active">
          <div className="description-content">
            <button
              className="close-popup"
              onClick={() => setShowDescriptionPopup(false)}
            >
              &times;
            </button>
            <h3>Product Description</h3>
            <div className="description-text">{currentDescription}</div>
          </div>
        </div>
      )}

      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">Confirm Deletion</div>
            <div className="modal-body">
              Are you sure you want to delete this product? This action cannot be undone.
            </div>
            <div className="modal-footer">
              <button
                className="modal-btn modal-btn-cancel"
                onClick={() => setShowDeleteModal(false)}
              >
                Cancel
              </button>
              <button
                className="modal-btn modal-btn-confirm"
                onClick={() => handleDelete(productToDelete)}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminProducts;
